import React, { useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import styled from "styled-components";

const Title = styled.h2`
  font-weight: 300;
`;

const CardContainer = styled.div`
  display: flex;
  flex-wrap: nowrap;
  overflow-x: auto;
  &::-webkit-scrollbar {
    display: none;
  }
`;

const Card = styled.div`
  background-color: gray;
  margin-right: 16px;
  width: 15rem;
  flex-shrink: 0;
  /* height:auto */
`;

const DogCarousel = () => {
  const [dogList, setDogList] = useState([]);
  const [dogNameList, setDogNameList] = useState([]);

  useEffect(() => {
    fetch("https://dog.ceo/api/breeds/list/all")
    .then((response) => response.json())
    .then((data) => {
      console.log(data.message);
      setDogNameList(Object.keys(data.message));
    }); 
  }, [])

  for (let i = 0; i < dogNameList.length; i++) {
    fetch(`https://dog.ceo/api/breed/${dogNameList[i]}/images/random`)
    .then((response) => response.json())
    .then((data) => {
        setDogList([...dogList,{img:data.message,name:dogNameList[i]}]);
    }); 

  }
  return (
    <Container fluid className="py-2" style={{ overflowX: "hidden" }}>
      <h2 className="font-weight-light m-4">
        Bootstrap 4 Horizontal Scrolling Cards with Flexbox
      </h2>
      <CardContainer className="d-flex flex-row"> 
        {dogList.map((data,index) => (
               <Card className="card card-body col-lg-3 col-md-4 col-sm-6 flex-shrink-0 mx-3">
               <img className="card-img-top" src={data} alt="Card image cap" />
               <div className="card-body">
                 <h5 className="card-title">Card title</h5>
               </div>
             </Card>
            ))} 


      </CardContainer>
    </Container>
  );
};

export default DogCarousel;
