import React, { useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import styled from "styled-components";

const Title = styled.h2`
  font-weight: 300;
`;

const CardContainer = styled.div`
  display: flex;
  flex-wrap: nowrap;
  overflow-x: auto;
  scrollbar-width: none;
  -ms-overflow-style: none;
  &::-webkit-scrollbar {
    width: 0;
    height: 0;
  }
`;

const Card = styled.div`
  background-color: gray;
  margin-right: 16px;
  width: 15rem;
  flex-shrink: 0;
`;

const DogCarousel = () => {
  const [dogList, setDogList] = useState([]);
  const [dogNameList, setDogNameList] = useState([]);

  useEffect(() => {
    fetch("https://dog.ceo/api/breeds/list/all")
      .then((response) => response.json())
      .then((data) => {
        console.log(data.message);
        setDogNameList(Object.keys(data.message));
      });
  }, []);

  useEffect(() => {
    const fetchDogList = async () => {
      const newDogList = [];
      for (let i = 0; i < dogNameList.length; i++) {
        const response = await fetch(
          `https://dog.ceo/api/breed/${dogNameList[i]}/images/random`
        );
        const data = await response.json();
        console.log(data);
        setDogList([...newDogList,{img: data.message, name: dogNameList[i]}]);
        // newDogList.push({ img: data.message, name: dogNameList[i] });
      }
      console.log(newDogList);
      setDogList(newDogList);
    };
    fetchDogList();
  }, [dogNameList]);

  return (
    <Container fluid className="py-2" style={{ overflowX: "hidden" }}>
      <Title className="font-weight-light m-4">
        Bootstrap 4 Horizontal Scrolling Cards with Flexbox
      </Title>
      <CardContainer className="d-flex flex-row">
        {dogList.map((data, index) => (
          <Card key={index} className="card card-body col-lg-3 col-md-4 col-sm-6 flex-shrink-0 mx-3">
            <img className="card-img-top" src={data.img} alt="Card image cap" />
            <div className="card-body">
              <h5 className="card-title">{data.name}</h5>
            </div>
          </Card>
        ))}
      </CardContainer>
    </Container>
  );
};

export default DogCarousel;
