import React, { useState, useEffect } from "react";
import DogCarousel from "./DogCarousel";
import { Container, Row, Col, Card, Button, Modal } from "react-bootstrap";
import styled from "styled-components";

const ImageCard = styled(Card)`
  margin-bottom: 20px;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
`;

const Image = styled.img`
  width: 100%;
`;

function Home() {
  // State for holding the selected breed
  const [selectedBreed, setSelectedBreed] = useState("");

  // State for holding the list of images
  const [imageList, setImageList] = useState([]);
  const [breedList, setBreedList] = useState([]);
  // State for controlling the modal
  const [showModal, setShowModal] = useState(false);
  const [modalImage, setModalImage] = useState("");

  // Fetch images of a selected breed
  useEffect(() => {
    if (selectedBreed) {
      fetch(`https://dog.ceo/api/breed/${selectedBreed}/images`)
        .then((response) => response.json())
        .then((data) => setImageList(data.message));
    } else {
      fetch("https://dog.ceo/api/breeds/image/random/50")
        .then((response) => response.json())
        .then((data) => setImageList([data.message]));
    }
  }, [selectedBreed, setImageList]);

  // Handle click on an image
  const handleImageClick = (image) => {
    setModalImage(image);
    setShowModal(true);
  };
  console.log(imageList);
  // Render images in odd-even layout
  const renderImages = () => {
    const containerWidth =
      document.getElementById("image-container").clientWidth;
    let rowHeight = 0;
    let currentRowWidth = 0;
    let currentRowIndex = 0;
    let oddRow = true;

    return imageList.map((image, index) => {
      const img = new Image();
      img.src = image;
      img.onload = () => {
        const aspectRatio = img.width / img.height;
        const imgWidth = rowHeight * aspectRatio;

        if (currentRowWidth + imgWidth > containerWidth) {
          currentRowIndex++;
          currentRowWidth = 0;
          oddRow = !oddRow;
        }

        const colSize = imgWidth < containerWidth / 4 ? 12 : 6;

        const isOdd = oddRow
          ? currentRowIndex % 2 === 0
          : currentRowIndex % 2 !== 0;

        const cardStyle = {
          gridRow: `${currentRowIndex + 1} / ${currentRowIndex + 2}`,
          gridColumn: `${isOdd ? "1 / 2" : "2 / 3"}`,
        };

        currentRowWidth += imgWidth;

        return (
          <Col xs={12} md={colSize} key={index}>
            <ImageCard style={cardStyle}>
              <Image
                src={image}
                alt="dog"
                onClick={() => handleImageClick(image)}
              />
            </ImageCard>
          </Col>
        );
      };
      return null;
    });
  };

  return (
    <Container className="my-5">
      <DogCarousel setSelectedBreed={setSelectedBreed} />
      <Row>{renderImages()}</Row>
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Body>
          <img src={modalImage} className="w-100" alt="dog" />
        </Modal.Body>
      </Modal>
    </Container>
  );
}

export default Home;
