import React, { useState, useEffect } from "react";
import DogCarousel from "./DogCarousel";
import { Container, Row, Col, Card, Button, Modal } from "react-bootstrap";
import styled from "styled-components";
import { useMediaQuery } from 'react-responsive';
const ImageCard = styled(Card)`
  margin-bottom: 20px;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
`;

const Image = styled.img`
  width: 100%;
`;

function Home() {
  // State for holding the selected breed
  const [selectedBreed, setSelectedBreed] = useState("");

  // State for holding the list of images
  const [imageList, setImageList] = useState([]);
  const [breedList, setBreedList] = useState([]);
  // State for controlling the modal
  const [showModal, setShowModal] = useState(false);
  const [modalImage, setModalImage] = useState("");

  // Fetch images of a selected breed
  useEffect(() => {
    if (selectedBreed) {
      fetch(`https://dog.ceo/api/breed/${selectedBreed}/images`)
        .then((response) => response.json())
        .then((data) => setImageList(data.message));
    } else {
      fetch("https://dog.ceo/api/breeds/image/random/50")
        .then((response) => response.json())
        .then((data) => setImageList([data.message]));
    }
  }, [selectedBreed, setImageList]);
console.log(imageList);
  // Handle click on an image
  const handleImageClick = (image) => {
    setModalImage(image);
    setShowModal(true);
  };

  // Render images in odd-even layout
  // Render images in odd-even layout
  const isSmallScreen = useMediaQuery({ query: '(max-width: 576px)' });
  const isMediumScreen = useMediaQuery({ query: '(min-width: 577px) and (max-width: 991px)' });
  const numColumns = isSmallScreen ? 2 : (isMediumScreen ? 3 : 4);

  // ...

  // Calculate the height of each row based on the tallest image in the row
  const renderImages = () => {
    const rowHeight = 300; // Change this to adjust the row height
    const imageRows = [];
    let currentRow = [];
    let tallestInRow = 0;
    imageList.forEach((image, index) => {
      const img = document.createElement("img");
      img.src = image;
      img.onload = () => {
        if (img.height > tallestInRow) {
          tallestInRow = img.height;
        }
        currentRow.push({ index, image, height: img.height, width: img.width });
        if (currentRow.length === numColumns) {
          imageRows.push({ images: currentRow, tallestInRow });
          currentRow = [];
          tallestInRow = 0;
        }
      };
    });

    // Render each row with the appropriate number of columns and odd-even positioning based on image height
    return imageRows.map((row, rowIndex) => (
      <Row key={rowIndex}>
        {row.images.map((imageData) => (
          <Col key={imageData.index} xs={12} md={Math.floor(12 / numColumns)}>
            <ImageCard>
              <Image
                src={imageData.image}
                alt="dog"
                onClick={() => handleImageClick(imageList[imageData.index])}
              />
            </ImageCard>
          </Col>
        ))}
        {numColumns - row.images.length > 0 && (
          <Col xs={12} md={Math.floor(12 / numColumns)}>
            {/* Add an empty column to fill out the row */}
          </Col>
        )}
      </Row>
    ));
  };


  return (
    <Container className="my-5">
      <DogCarousel setSelectedBreed={setSelectedBreed} />
      {imageList.length > 0 && renderImages()}
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Body>
          <img src={modalImage} className="w-100" alt="dog" />
        </Modal.Body>
      </Modal>
    </Container>
  );
}

export default Home;
