import React, { useState, useEffect } from "react";
import DogCarousel from "./DogCarousel";
import { Container, Row, Col, Card, Button, Modal } from "react-bootstrap";
import styled from "styled-components";

const ImageCard = styled(Card)`
  margin-bottom: 20px;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
`;

const Image = styled.img`
  width: 100%;
`;

function Home() {
  // State for holding the selected breed
  const [selectedBreed, setSelectedBreed] = useState("");

  // State for holding the list of images
  const [imageList, setImageList] = useState([]);
  const [breedList, setBreedList] = useState([]);
  // State for controlling the modal
  const [showModal, setShowModal] = useState(false);
  const [modalImage, setModalImage] = useState("");

  // Fetch images of a selected breed
  useEffect(() => {
    if (selectedBreed) {
      fetch(`https://dog.ceo/api/breed/${selectedBreed}/images`)
        .then((response) => response.json())
        .then((data) => setImageList(data.message));
    } else {
      fetch("https://dog.ceo/api/breeds/image/random/50")
        .then((response) => response.json())
        .then((data) => setImageList([data.message]));
    }
  }, [selectedBreed, setImageList]);

  // Handle click on an image
  const handleImageClick = (image) => {
    setModalImage(image);
    setShowModal(true);
  };
  console.log(imageList);
  // Render images in odd-even layout

  return (
    <Container className="my-5">
      <DogCarousel setSelectedBreed={setSelectedBreed} />
      <div class="container">
        <div class="row">
          <div class="col-lg-3 col-md-4 col-sm-6">
            <div class="card">
              <img
                class="card-img-top img-fluid"
                src="//placeimg.com/640/480/any"
                alt="Card image cap"
              />
              <div class="card-body">
                <h4 class="card-title">Card title that wraps to a new line</h4>
                <p class="card-text">
                  This is a longer card with supporting text below as a natural
                  lead-in to additional content. This content is a little bit
                  longer.
                </p>
              </div>
            </div>
          </div>
          <div class="col-lg-3 col-md-4 col-sm-6">
            <div class="card card-body">
              <blockquote class="card-blockquote">
                <p>
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                  Integer posuere erat a ante.
                </p>
                <footer>
                  <small class="text-muted">
                    Someone famous in{" "}
                    <cite title="Source Title">Source Title</cite>
                  </small>
                </footer>
              </blockquote>
            </div>
          </div>
        </div>
      </div>
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Body>
          <img src={modalImage} className="w-100" alt="dog" />
        </Modal.Body>
      </Modal>
    </Container>
  );
}

export default Home;
