import React, { useState, useEffect } from "react";
import { Container, Row, Col, Card, Button, Modal } from "react-bootstrap";
function Home() {
  // State for holding the selected breed
  const [selectedBreed, setSelectedBreed] = useState("");

  // State for holding the list of images
  const [imageList, setImageList] = useState([]);
  const [breedList, setBreedList] = useState([]);
  // State for controlling the modal
  const [showModal, setShowModal] = useState(false);
  const [modalImage, setModalImage] = useState("");

  // Fetch a random dog image on component mount
  useEffect(() => {
    fetch("https://dog.ceo/api/breeds/list/all")
      .then((response) => response.json())
      .then((data) => {
        console.log(data.message);
        setBreedList(Object.keys(data.message));
      });
  }, []);

  // Fetch images of a selected breed
  //   useEffect(() => {
  //     if (selectedBreed) {
  //       fetch(`https://dog.ceo/api/breed/${selectedBreed}/images`)
  //         .then((response) => response.json())
  //         .then((data) => setImageList(data.message));
  //     } else {
  //       fetch("https://dog.ceo/api/breeds/image/random")
  //         .then((response) => response.json())
  //         .then((data) => setImageList([data.message]));
  //     }
  //   }, [selectedBreed]);

  // Handle click on an image
  const handleImageClick = (image) => {
    setModalImage(image);
    setShowModal(true);
  };

  return (
    <Container className="my-5">
      <Row>
        <Col md={4}>
          <h2>Choose a breed:</h2>
          <Card>
            <Card.Body>
              <Button
                onClick={() => setSelectedBreed("")}
                variant="outline-primary"
              >
                Random
              </Button>{" "}
              <Button
                onClick={() => setSelectedBreed("labrador")}
                variant="outline-primary"
              >
                Labrador
              </Button>{" "}
              <Button
                onClick={() => setSelectedBreed("husky")}
                variant="outline-primary"
              >
                Husky
              </Button>{" "}
              <Button
                onClick={() => setSelectedBreed("poodle")}
                variant="outline-primary"
              >
                Poodle
              </Button>
            </Card.Body>
          </Card>
        </Col>


        <div className="d-flex flex-column">
          <div className="d-flex align-items-center justify-content-between p-2">
            {/* <h3 className="mb-0">{name}</h3> */}
            <button className="btn btn-link">View All</button>
          </div>
          <div
            className="overflow-auto p-2"
            style={{ backgroundColor: "#f8f9fa", borderRadius: "10px" }}
          >
            <div className="d-flex flex-row flex-nowrap">
              {breedList.map((item, index) => (
                <div key={index} className="p-2">
                  <div
                    className="rounded bg-secondary d-flex align-items-center justify-content-center"
                    style={{ width: "50px", height: "50px" }}
                  >
                    <p className="text-center m-0">{item}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </Row>
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Body>
          <img src={modalImage} className="w-100" alt="dog" />
        </Modal.Body>
      </Modal>
    </Container>
  );
}

export default Home;
