import React, { useState, useEffect } from "react";
import DogCarousel from "./DogCarousel";
import { Container, Modal } from "react-bootstrap";
import Masonry, { ResponsiveMasonry } from "react-responsive-masonry";

function Home() {
  // State for holding the selected breed
  const {
    selectBreed,
    setSelectBreed,
    selectBreedIndex,
    setSelectBreedIndex,
  } = useContext(BreedContext);

  // State for holding the list of images
  const [imageList, setImageList] = useState([]);
  // State for controlling the modal
  const [showModal, setShowModal] = useState(false);
  const [modalImage, setModalImage] = useState("");
  const [zoom, setZoom] = useState(false);

  const handleZoomIn = () => {
    setZoom(true);
  };

  const handleZoomOut = () => {
    setZoom(false);
  };
  // Fetch images of a selected breed
  useEffect(() => {
    if (selectedBreed) {
      fetch(`https://dog.ceo/api/breed/${selectedBreed}/images`)
        .then((response) => response.json())
        .then((data) => setImageList(data.message));
    } else {
      fetch("https://dog.ceo/api/breeds/image/random/50")
        .then((response) => response.json())
        .then((data) => setImageList([data.message][0]));
    }
  }, [selectedBreed, setImageList]);

  // Handle click on an image
  const handleImageClick = (image) => {
    setModalImage(image);
    setShowModal(true);
  };

  // Render images in odd-even layout

  return (
    <Container className="mt-3">
      <DogCarousel setSelectedBreed={setSelectedBreed} />
      <div className="container my-4">
        <h3
          className="mb-4"
          style={selectedBreed !== "" ? null : { visibility: "hidden" }}
        >
          {selectedBreed + " Breed : " + imageList.length + " Images"}
        </h3>
        <ResponsiveMasonry columnsCountBreakPoints={{ 350: 2, 750: 3, 900: 4 }}>
          <Masonry gutter="20px" >
            {imageList.map((data, index) => (
              <img
                key={index}
                src={data}
                alt={`image-${index}`}
                onClick={() => {
                  handleImageClick(data);
                }}
                // className="border"
                style={{ border: "1vw solid gray", borderRadius: "5vh" }}
              />
            ))}
          </Masonry>
        </ResponsiveMasonry>
      </div>
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Header closeButton></Modal.Header>
        <Modal.Body>
          <div style={{ overflow: "auto" }}>
            <img
              src={modalImage}
              className="w-100"
              alt="dog"
              style={{ transform: zoom ? "scale(1.5)" : "scale(1)" }}
              onClick={handleZoomIn}
              onDoubleClick={handleZoomOut}
            />
          </div>
        </Modal.Body>
      </Modal>
    </Container>
  );
}

export default Home;
