import React, { useState, useEffect } from "react";
import DogCarousel from "./DogCarousel";
import { Container, Row, Col, Card, Button, Modal } from "react-bootstrap";
import styled from "styled-components";

const ImageCard = styled(Card)`
  margin-bottom: 20px;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
`;

const Image = styled.img`
  width: 100%;
`;

function Home() {
  // State for holding the selected breed
  const [selectedBreed, setSelectedBreed] = useState("");

  // State for holding the list of images
  const [imageList, setImageList] = useState([]);
  const [breedList, setBreedList] = useState([]);
  // State for controlling the modal
  const [showModal, setShowModal] = useState(false);
  const [modalImage, setModalImage] = useState("");

  // Fetch images of a selected breed
  useEffect(() => {
    if (selectedBreed) {
      fetch(`https://dog.ceo/api/breed/${selectedBreed}/images`)
        .then((response) => response.json())
        .then((data) => setImageList(data.message));
    } else {
      fetch("https://dog.ceo/api/breeds/image/random/50")
        .then((response) => response.json())
        .then((data) => setImageList([data.message]));
    }
  }, [selectedBreed, setImageList]);

  // Handle click on an image
  const handleImageClick = (image) => {
    setModalImage(image);
    setShowModal(true);
  };
  console.log(imageList);
  // Render images in odd-even layout
  // Render images in odd-even layout
  const renderImages = () => {
    const colCount = window.innerWidth < 768 ? 1 : window.innerWidth < 992 ? 2 : 3;
    const rowHeight = window.innerWidth < 768 ? 250 : 350;
    const imageCount = imageList.length;
    const rowCount = Math.ceil(imageCount / colCount);
    const imagesPerRow = Math.ceil(imageCount / rowCount);
    const style = {
      display: "grid",
      gridTemplateColumns: `repeat(${imagesPerRow}, 1fr)`,
      gap: "20px",
    };
    const rows = [];
    for (let i = 0; i < rowCount; i++) {
      const row = [];
      for (let j = 0; j < imagesPerRow; j++) {
        const index = i * imagesPerRow + j;
        if (index < imageCount) {
          const img = document.createElement("img");
          img.src = imageList[index];
          img.onload = () => {
            if (img.height > rowHeight) {
              const aspectRatio = img.width / img.height;
              img.height = rowHeight;
              img.width = rowHeight * aspectRatio;
            }
          };
          row.push(
            <div key={index}>
              <ImageCard>
                <Image
                  src={imageList[index]}
                  alt="dog"
                  onClick={() => handleImageClick(imageList[index])}
                />
              </ImageCard>
            </div>
          );
        }
      }
      rows.push(
        <div style={style} key={i}>
          {row}
        </div>
      );
    }
    return rows;
  };


  return (
    <Container className="my-5">
      <DogCarousel setSelectedBreed={setSelectedBreed} />
      {imageList.length > 0 && renderImages()}
      <Modal show={showModal} onHide={() => setShowModal(false)} size="lg">
        <Modal.Body>
          <img src={modalImage} className="w-100" alt="dog" />
        </Modal.Body>
      </Modal>
    </Container>
  );
}

export default Home;
