import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "../context/BreedContext";
import { ListGroup, Modal, Button } from "react-bootstrap";

function SubBreeds() {
  const { selectBreed } = useContext(AuthContext);
  const [breeds, setBreeds] = useState([]);
  const [selectedBreed, setSelectedBreed] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    if (selectBreed !== "") {
      fetch(`https://dog.ceo/api/breed/${selectBreed}/list`)
        .then((response) => response.json())
        .then((data) => setBreeds(data.message))
        .catch((error) => console.log(error));
    }
  }, [selectBreed]);

  const handleCloseModal = () => setShowModal(false);

  const handleShowModal = (breed) => {
    setSelectedBreed(breed);
    setShowModal(true);
  };

  return (
    <div className="d-flex justify-content-center mt-3">
      {selectBreed === "" && (
        <h1 className="text-center mt-4">Please Select Breed</h1>
      )}
      {selectBreed !== "" && (
        <>
          <div style={{ width: "90%", margin: "auto", marginTop: "7vh" }}>
            <h2 style={{ marginBottom: "5vh" }}>{selectBreed} Sub Breeds</h2>
            <ListGroup>
              <ListGroup.Item style={{ backgroundColor: "#f8f9fa" }}>
                <div className="d-flex justify-content-between">
                  <div>SUB-BREED</div>
                  <div>OPEN 1 IMAGE (MODAL)</div>
                </div>
              </ListGroup.Item>
              {breeds.map((breed, index) => (
                <ListGroup.Item
                  key={index}
                  style={{ border: "none", cursor: "pointer" }}
                  onClick={() => handleShowModal(breed)}
                >
                  <div className="d-flex justify-content-between">
                    <div>{breed}</div>
                    <div>Other name</div>
                  </div>
                </ListGroup.Item>
              ))}
            </ListGroup>
          </div>
          {selectedBreed && (
            <Modal show={showModal} onHide={handleCloseModal}>
              <Modal.Header closeButton>
                <Modal.Title>{selectedBreed}</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <img
                  src={`https://dog.ceo/api/breed/${selectBreed}/${selectedBreed}/images/random`}
                  alt={`${selectedBreed}`}
                  style={{ width: "100%" }}
                />
              </Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={handleCloseModal}>
                  Close
                </Button>
              </Modal.Footer>
            </Modal>
          )}
        </>
      )}
    </div>
  );
}

export default SubBreeds;
